mapper.select:

SELECT
	batch_id,batch_sn,batch_name,batch_memo,batch_shipper,card_amount,org_id,
	user_id,alter_id,province_id,city_id,district_id,clw_batch_id,gprs_amount,
	allot_month,allot_value,allot_reset,live_month,give_value,give_live_month,
	wszl_value,wszl_live_month,bind_value,bind_live_month,time_added,time_modify
FROM
	cc_gprs_batch
WHERE
    1=1



		AND org_id = #{org_id}







				AND org_id IN

					#{id}









	AND batch_sn LIKE #{batch_sn_bind}



	  AND time_added >= #{date_start} 



	 AND time_added <= #{date_end}
