mapper.select:

SELECT GG.gift_id,GG.user_id,GG.card_id,GG.card_iccid,GG.gprs_amount,GG.allot_month,
	GG.allot_value,GG.allot_reset,GG.live_month,GG.gift_name,GG.time_added,GG.time_expire,
	GC.org_id,GG.create_by
FROM
	cc_gprs_gift AS GG
INNER JOIN `cc_gprs_card` GC ON GC.`card_id` = GG.`card_id` 
WHERE
	1 = 1




		AND GC.org_id = #{org_id}







				AND GC.org_id IN

					#{id}









	AND GG.card_iccid LIKE #{card_iccid_bind}



	  AND GG.time_added >= #{date_start} 



	 AND GG.time_added <= #{date_end}
