mapper.select:
SELECT SL.card_id, C.card_iccid, C.card_type, C.org_id, COUNT(SL.card_id) AS stop_num, ( SELECT COUNT(pay_id) FROM cc_gprs_pay WHERE card_id = SL.card_id AND is_paid = 1 AND pay_method > 0 ) AS pay_count, SL.balance_value AS stop_value, C.time_paid, C.time_stop, C.time_last FROM ( SELECT * FROM cc_onoff_log WHERE onoff_type = 1 ORDER BY onoff_id DESC ) SL INNER JOIN `cc_gprs_card` C ON C.card_id = SL.card_id
AND C.org_id = #{org_id}
AND C.org_id IN
#{id}
AND C.card_type = #{card_type}
AND C.card_iccid LIKE #{card_iccid_bind}
GROUP BY SL.card_id

SELECT COUNT(*) FROM (	

		SELECT
			1
		FROM
			(
				SELECT	*
				FROM cc_onoff_log
				WHERE onoff_type = 1
				ORDER BY onoff_id DESC
			) SL
		INNER JOIN `cc_gprs_card` C ON C.card_id = SL.card_id




				AND C.org_id = #{org_id}







						AND C.org_id IN

							#{id}








			AND C.card_type = #{card_type}




			AND C.card_iccid LIKE #{card_iccid_bind}


		GROUP BY	SL.card_id 

		)
