mapper.select:

SELECT R.card_id,R.user_id,R.device_sn,R.card_iccid,R.owner_name,R.owner_gender,R.owner_cdi,
	R.cdi_img1,R.cdi_img2,R.cdi_img3,R.cdi_status,R.give_value,R.give_time,R.time_added,R.time_modify,
	R.time_audit,C.org_id, C.time_last, C.time_active
FROM
	cc_realname AS R
INNER JOIN cc_gprs_card C ON C.card_id = R.card_id
WHERE
	1 = 1




		AND C.org_id = #{org_id}







				AND C.org_id IN

					#{id}









	AND R.card_iccid LIKE #{card_iccid_bind}



	  AND R.time_added >= #{date_start} 



	 AND R.time_added <= #{date_end} 



	 AND R.cdi_status <= #{status}


SELECT COUNT(RL.card_id) FROM cc_realname RL INNER JOIN cc_gprs_card GC ON GC.card_id = RL.card_id WHERE RL.cdi_status = 2






		AND GC.org_id IN

			#{id}
