mapper.select:

SELECT time_added,user_name,user_id,owner_wszl,owner_real,owner_bind,
	batch_time,batch_id,card_status,card_unused,card_used,card_iccid,card_id,
	org_id,reset_id
FROM
	cc_reset_log
WHERE
	1 = 1



		AND org_id = #{org_id}







				AND org_id IN

					#{id}









	AND card_iccid LIKE #{card_iccid_bind}



	  AND time_added >= #{date_start} 



	 AND time_added <= #{date_end}
